import React, { useEffect, useState } from "react";
import axios from "axios";
import {
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    Button,
    Paper,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    TextField,
} from "@mui/material";

const StockTable = () => {
    const [stocks, setStocks] = useState([]);
    const [editStock, setEditStock] = useState(null); // For managing the stock being edited

    // Fetch all stocks from the backend
    useEffect(() => {
        const fetchStocks = async () => {
            try {
                const response = await axios.get("/api/stocks");
                setStocks(response.data);
            } catch (error) {
                console.error("Error fetching stocks:", error);
            }
        };
        fetchStocks();
    }, []);

    // Handle delete operation
    const handleDelete = async (id) => {
        try {
            await axios.delete(`/api/stocks/${id}`);
            setStocks(stocks.filter((stock) => stock.id !== id));
        } catch (error) {
            console.error("Error deleting stock:", error);
        }
    };

    // Open the edit dialog
    const handleEditOpen = (stock) => {
        setEditStock({ ...stock }); // Clone the stock object to allow edits
    };

    // Close the edit dialog
    const handleEditClose = () => {
        setEditStock(null);
    };

    // Handle submitting the edited stock
    const handleEditSubmit = async () => {
        try {
            await axios.put(`/api/stocks/${editStock.id}`, editStock);
            setStocks(
                stocks.map((stock) =>
                    stock.id === editStock.id ? editStock : stock
                )
            );
            handleEditClose();
        } catch (error) {
            console.error("Error updating stock:", error);
        }
    };

    return (
        <TableContainer component={Paper} sx={{ maxWidth: 800, margin: "auto", mt: 5 }}>
            <Table>
                <TableHead>
                    <TableRow>
                        <TableCell>Name</TableCell>
                        <TableCell>Ticker</TableCell>
                        <TableCell>Quantity</TableCell>
                        <TableCell>Buy Price</TableCell>
                        <TableCell>Actions</TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {stocks.map((stock) => (
                        <TableRow key={stock.id}>
                            <TableCell>{stock.name}</TableCell>
                            <TableCell>{stock.ticker}</TableCell>
                            <TableCell>{stock.quantity}</TableCell>
                            <TableCell>{stock.buyPrice}</TableCell>
                            <TableCell>
                                <Button
                                    variant="contained"
                                    color="primary"
                                    onClick={() => handleEditOpen(stock)}
                                    sx={{ mr: 1 }}
                                >
                                    Edit
                                </Button>
                                <Button
                                    variant="contained"
                                    color="error"
                                    onClick={() => handleDelete(stock.id)}
                                >
                                    Delete
                                </Button>
                            </TableCell>
                        </TableRow>
                    ))}
                </TableBody>
            </Table>

            {/* Edit Dialog */}
            {editStock && (
                <Dialog open={true} onClose={handleEditClose}>
                    <DialogTitle>Edit Stock</DialogTitle>
                    <DialogContent>
                        <TextField
                            label="Stock Name"
                            fullWidth
                            margin="normal"
                            value={editStock.name}
                            onChange={(e) =>
                                setEditStock({ ...editStock, name: e.target.value })
                            }
                        />
                        <TextField
                            label="Ticker"
                            fullWidth
                            margin="normal"
                            value={editStock.ticker}
                            onChange={(e) =>
                                setEditStock({ ...editStock, ticker: e.target.value })
                            }
                        />
                        <TextField
                            label="Quantity"
                            type="number"
                            fullWidth
                            margin="normal"
                            value={editStock.quantity}
                            onChange={(e) =>
                                setEditStock({ ...editStock, quantity: e.target.value })
                            }
                        />
                        <TextField
                            label="Buy Price"
                            type="number"
                            fullWidth
                            margin="normal"
                            value={editStock.buyPrice}
                            onChange={(e) =>
                                setEditStock({ ...editStock, buyPrice: e.target.value })
                            }
                        />
                    </DialogContent>
                    <DialogActions>
                        <Button onClick={handleEditClose} color="secondary">
                            Cancel
                        </Button>
                        <Button onClick={handleEditSubmit} variant="contained" color="primary">
                            Save
                        </Button>
                    </DialogActions>
                </Dialog>
            )}
        </TableContainer>
    );
};

export default StockTable;
